#!ruby
t1 = Time.now
ADJACENT=13
$number =

''

array = $number.split('').map { |e| e.to_i }
index =0
max_product = array[0,ADJACENT].inject(1) { |mem, var|  mem*=var}
product = max_product
i=1
loop do
	break if i >= array.size-ADJACENT
	sub_array = array[i,ADJACENT]
	zero_index = sub_array.rindex(0)
	if not zero_index.nil?
		i+=zero_index+1
		next
	end

	product = sub_array.inject(1) { |mem, var|  mem*=var}
	if product>max_product
		max_product = product
		index = i
	end
	i+=1
end
puts max_product

t2 = Time.now
puts "Time Elapsed #{'%0.2f'%((t2-t1)*1000)} ms"
